import React from "react";
import { Box, Title, Typography, Input, Button, Grid, Link } from "antd";
import { UserOutlined } from "@ant-design/icons";
import "../css/SignIn.css"; // CSS 파일 경로
import axios from "axios";


const SignIn = () => {
  const handleSubmit = (event) => {
    console.log("함수 실행");
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const userInfo = {
      email: data.get("email"),
      password: data.get("password"),
    };
    console.log(userInfo);
    axios
      .post("/signin", userInfo)
      .then((res) => {
        console.log(res.data);
        // 로그인 성공 시 실행될 코드
      })
      .catch((err) => {
        console.error(err);
        // 로그인 실패 시 실행될 코드
      });
  };

  return (
    <Box className="sign-in-container">
      <div className="text-center">
        {/* <LockOutlinedIcon /> */}
      </div>
      <Title level={3} className="text-center">
        Sign in
      </Title>
      <Box component="form" noValidate onSubmit={handleSubmit} className="form">
        <Input
          required
          className="full-width-input"
          placeholder="학번"
          prefix={<UserOutlined />}
          autoComplete="family-name"
        />
        <Input.Password
          className="full-width-input"
          placeholder="Password"
          autoComplete="current-password"
        />
        <Button type="primary" htmlType="submit" className="submit-button" block>
          Sign in
        </Button>
      </Box>
      <Grid item>
        <nav>
          <Link to="/signup">회원가입</Link>
        </nav>
      </Grid>
    </Box>
  );
};

export default SignIn;
